import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {useDispatch} from "react-redux";
import {logout} from "../store/auth";
import {Image, Nav} from "react-bootstrap";
import Slider from "react-slick";
import settingService from "../services/settingService";
const SuggestProduct = (props) => {
    const id = 97;
    const [suggestProduct,setSuggestProduct] = useState([])
    const settings1 = {
        slidesToShow: 6,
        swipeToSlide: true,
        focusOnSelect: true,
        infinite: true,
        nextArrow: (
            <div>
                <div className="next-slick-arrow"><i className="fa fa-angle-right" aria-hidden="true"></i> </div>
            </div>
        ),
        prevArrow: (
            <div>
                <div className="prev-slick-arrow"><i className="fa fa-angle-left" aria-hidden="true"></i> </div>
            </div>
        ),
    };
    useEffect(() => {
        settingService.getWidget(id).then((res) => {
                console.log(res.data)
            setSuggestProduct(res.data)
        }
        );
    }, [id]);
    return (
        <>
            <div className="block productCat">
                <section className="featured-products  mt-3">
                    <div className="latest-block-list latest-home-block-list" id="flash-sale">
                        <div className="container block-list">
                            <div className="row">

                                <div className="element-grid ">
                                    <div  className="tb-products-grid ">
                                        <Slider {...settings1} >
                                            {
                                                suggestProduct.map((aSale, idx) => (
                                                    <div className="product-item" style={{padding: "8px"}}>
                                                        <a className="gallery"
                                                           href="https://www.kkfashion.vn/dam-hoa-dang-xoe-xep-tang-tay-dai-kk117-02?rec_sid=JqaLrnHDRkKkGHptnyF7Ny&amp;rec_mid=386&amp;rec_pid=KK117-02&amp;rec_id=a6c07426-d7df-4d91-a809-569171a7f2b0"
                                                           tabIndex="0">
                                                            <Image width="90%"
                                                                 src="https://media.kkfashion.vn/AppierFeed/KK117-02.1.jpg?w=656"
                                                                 alt="Đầm hoa dáng xòe xếp tầng tay dài" /></a>
                                                        <div className="product-content">
                                                            <div className="item-top">
                                                                <p className="code-title fontutm">
                                                                    <a
                                                                        href="https://www.kkfashion.vn/dam-hoa-dang-xoe-xep-tang-tay-dai-kk117-02?rec_sid=JqaLrnHDRkKkGHptnyF7Ny&amp;rec_mid=386&amp;rec_pid=KK117-02&amp;rec_id=a6c07426-d7df-4d91-a809-569171a7f2b0"
                                                                        tabIndex="0">KK117-02</a>
                                                                </p>
                                                                <span
                                                                className="price"><span>490.000 đ</span></span></div>
                                                            <div className="item-top"><h3 className="product-title text-left"><a
                                                                href="https://www.kkfashion.vn/dam-hoa-dang-xoe-xep-tang-tay-dai-kk117-02?rec_sid=JqaLrnHDRkKkGHptnyF7Ny&amp;rec_mid=386&amp;rec_pid=KK117-02&amp;rec_id=a6c07426-d7df-4d91-a809-569171a7f2b0"
                                                                tabIndex="0">Đầm hoa dáng xòe xếp tầng tay dài</a></h3></div>
                                                        </div>
                                                    </div>
                                                ))
                                            }


                                        </Slider>

                                    </div>

                                </div>

                            </div>
                        </div>
                    </div>
                </section>
            </div>

        </>
    );
};

export default SuggestProduct;
