import React, {useEffect, useState} from "react";
import categoryService from "../services/categoryService";
import {Link, useLocation, useParams} from "react-router-dom";
import {Image} from "react-bootstrap";
import productService from "../services/productService";
const ListProduct = (props) => {
    const [items, setItems] = useState([]);
    const [page, setPage] = useState(1);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [id, setId] = useState("");
    const [title, setTitle] = useState("");
    const [price, setPrice] = useState("");
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const query = searchParams.get("q");

    useEffect(() => {
        setTitle(query)

        productService.list(page,id,title,price).then((res) => {
            setItems(res.items_prd.data);
            setTotalPages(res.items_prd.last_page);
            setCurrentPage(res.items_prd.current_page);
        });
    }, [page,id,title,price,query]);
    function handlePageChange(newPage) {
        setPage(newPage);
    }

    function Pagination({ totalPages, currentPage, onPageChange }) {
        const pages = [];
        for (let i = 1; i <= totalPages; i++) {
            pages.push(
                <li key={i} className={i === currentPage ? 'active' : ''}>
                    <a href="#" onClick={() => onPageChange(i)}>
                        {i}
                    </a>
                </li>
            );
        }

        return (
            <nav className="pagination-number">
                <div className="text-center mgt20">
                    <ul className="page-list clearfix text-sm-center page-numbers">
                        {pages}
                    </ul>
                </div>
            </nav>
        )
    }
    return (
        <>
            {/*breadcrums*/}
            <div className="page-header single-breadcrums hidden-sm-down" style={{paddingTop: "40px"}} >
                <div className="page-breadcrumbs">
                    <nav data-depth="4" className="breadcrumbs">
                        <ol itemScope="" itemType="http://schema.org/BreadcrumbList">
                            <li itemProp="itemListElement" itemScope="" itemType="http://schema.org/ListItem">
                                <a itemProp="item" href="https://www.kkfashion.vn/">
                                    <span itemProp="name">Trang ch</span>
                                </a>
                                <meta itemProp="position" content="1" />
                                    <span className="sep">/</span>
                            </li>
                            <li itemProp="itemListElement" itemScope="" itemType="http://schema.org/ListItem">
                                <a itemProp="item" href="https://www.kkfashion.vn/thoi-trang-cong-so">
                                    <span itemProp="name">Thi trang cng s</span>
                                </a>
                                <meta itemProp="position" content="2" />
                                    <span className="sep">/</span>
                            </li>
                            <li itemProp="itemListElement" itemScope="" itemType="http://schema.org/ListItem">
                                <a itemProp="item" href="https://www.kkfashion.vn/chan-vay-cong-so">
                                    <span itemProp="name">Chn vy</span>
                                </a>
                                <meta itemProp="position" content="3" />
                                    <span className="sep">/</span>
                            </li>
                            <li itemProp="itemListElement" itemScope="" itemType="http://schema.org/ListItem">
                                <span itemProp="name" className="last">Chn vy midi xe xp ly ha tit caro</span>
                                <meta itemProp="position" content="4" />
                            </li>
                        </ol>
                    </nav>
                </div>
            </div>
            <div className="content">
                <div className="container">
                    <div className="row">
                        <div className="product-items col-sm-12">
                            {/*Tn danh mc*/}

                            <section className="form-fields">
                                <div className="row">
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label className="form-control-label ">
                                                Gi
                                            </label>
                                            <select className="form-control "
                                                    name="price"
                                                    value={price}
                                                    onChange={(e) => {
                                                        // eslint-disable-next-line no-unused-expressions
                                                        setPrice(e.target.value),
                                                            setPage(1)
                                                        }
                                                    }

                                            >
                                                <option value="">Chn mc gi</option>
                                                <option
                                                    value="0-500000">
                                                    Di 500.000
                                                </option>
                                                <option value="500000-1000000" >
                                                   T 500.000 n 1.000.000
                                                </option>
                                                <option value="1000000-1500000" >
                                                    T 1.000.000 n 1.500.000
                                                </option>
                                                <option value="1500000-2000000" >
                                                    T 1.500.000 n 2.000.000
                                                </option>
                                                <option value="2000000-2500000" >
                                                    T 2.000.000 n 2.500.000
                                                </option>

                                                <option value="2500000-3000000" >
                                                    T 2.500.000 n 3.000.000
                                                </option>

                                            </select>

                                        </div>
                                    </div>

                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <label className="form-control-label ">
                                                M sn phm
                                            </label>
                                            <input className="form-control"
                                                   name="id"
                                                   type="text"
                                                   value={id}
                                                   placeholder="Nhp m sn phm"
                                                   onChange={(e) =>
                                                       {
                                                           // eslint-disable-next-line no-unused-expressions
                                                           setId(e.target.value),
                                                           setPage(1)
                                                       }
                                                    }
                                            />
                                        </div>
                                    </div>

                                </div>


                            </section>
                            {/*List san pham*/}
                            <section id="products">
                                <div className="products-list jws-masonry">
                                    <div className="grid-sizer size-3"></div>

                                    <div id="js-product-list">
                                        <div className="products">
                                            { items.map((item, idx) => (
                                                <div className="tb-products-grid col-md-3 col-sm-6 col-xs-6">
                                                    <div className="product-item">
                                                        <div className="product-thumb">
                                                            <Image
                                                                sizes="(min-width: 64rem) 33.33vw, (min-width: 0rem) 50vw"
                                                                src={`https://shop.decor.tichhop.pro/storage${item.image}`}

                                                                alt={item.title}
                                                            />
                                                            <a className="gallery"
                                                               href="https://www.kkfashion.vn/ao-kieu-co-tron-sat-nach-dang-rong-asm13-23">
                                                                <Image
                                                                    sizes="(min-width: 64rem) 33.33vw, (min-width: 0rem) 50vw"
                                                                    src={`https://shop.decor.tichhop.pro/storage${item.image}`}
                                                                    alt="o kiu c trn st nch dng rng"
                                                                />
                                                                {/*<div className="kk-size hidden-xs hidden-sm">*/}
                                                                {/*    <div className="size">*/}
                                                                {/*        <span className="pull-left">Size</span>*/}
                                                                {/*        <div className="pull-right">*/}
                                                                {/*            <ul>*/}
                                                                {/*                <li>*/}
                                                                {/*                    S*/}
                                                                {/*                </li>*/}
                                                                {/*                <li>*/}
                                                                {/*                    M*/}
                                                                {/*                </li>*/}
                                                                {/*                <li>*/}
                                                                {/*                    L*/}
                                                                {/*                </li>*/}
                                                                {/*            </ul>*/}
                                                                {/*        </div>*/}
                                                                {/*    </div>*/}
                                                                {/*</div>*/}
                                                            </a>
                                                        </div>
                                                        <div className="product-content">
                                                            <div className="item-top">
                                                                <p className="code-title fontutm">
                                                                    <Link
                                                                        to={`/${categoryProduct.slug}/${item.slug}`}>#{item.id}</Link>
                                                                </p>
                                                                <span className="price">
                                                                    <span>{Intl.NumberFormat().format(item.price)} </span>
                                                                </span>
                                                            </div>
                                                            <div className="item-top">
                                                                <h3 className="product-title text-left">
                                                                    <Link to={`/${categoryProduct.slug}/${item.slug}`}>
                                                                        {item.title}
                                                                    </Link>
                                                                </h3>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                )
                                            )}

                                        </div>
                                        <Pagination totalPages={totalPages} currentPage={page} onPageChange={handlePageChange} />

                                        {/*<nav className="pagination-number">*/}
                                        {/*    <div className="text-center mgt20">*/}
                                        {/*        <ul className="page-list clearfix text-sm-center page-numbers">*/}
                                        {/*            <li className="current">*/}
                                        {/*                <a rel="nofollow" href="https://www.kkfashion.vn/ao"*/}
                                        {/*                   className="disabled js-search-link">*/}
                                        {/*                    1*/}
                                        {/*                </a>*/}
                                        {/*            </li>*/}
                                        {/*            <li>*/}
                                        {/*                <a rel="nofollow" href="https://www.kkfashion.vn/ao?page=2"*/}
                                        {/*                   className="js-search-link">*/}
                                        {/*                    2*/}
                                        {/*                </a>*/}
                                        {/*            </li>*/}
