import React, { useRef, useState, useEffect } from "react";
import "./Login.css";
import Input from "./../components/Input";
import { useNavigate } from "react-router-dom";
import userService from "../services/userService";
import {useDispatch} from "react-redux";
import {login} from "../store/auth";
import CustomButton from "../components/CustomButton";
import Header from "../containers/Header";

const Login = (props) => {
  const [message, setMessage] = useState(""); // state
  const [isWaiting, setIsWaiting] = useState(false); // state
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const usernameRef = useRef(); //tạo ra Ref gán vào usernameref
  const passwordRef = useRef(); //tạo ra Ref gán vào passwordref


  const submitHandler = (e) => {
    e.preventDefault(); // ngăn chặn refesh khi submit mặc định trên form, thẻ a > di chuyển trang;

    const username = usernameRef.current.value; //this.usernameRef.current tương đương vs document.getElementById(username)
    const password = passwordRef.current.value;
    console.log(username, password);
    // setIsWaiting(true)
    userService.login(username, password).then((res) => {
      setIsWaiting(false);
      console.log(res)
      if (res.status === 1) {
        setMessage("");
        dispatch(login({
          token: res.token,
          userInfo: res.user
        }))
        navigate("/");
      } else {
        setMessage(res.message);
      }
    });
    // if (username === "admin" && password === "123456") {
    //   setMessage('Ok');
    // } else {
    //   setMessage('Bad');
    // }
  };

  // document.getElementById('txtUsername').focus();

  useEffect(() => {
    usernameRef.current.focus();
  }, []); //! focus là thẻ input username khi render lại hoặc lần đầu truy cập

  return (
    <>
      <Header />
      <aside id="notifications" className="container"></aside>
      <div className="content">
        <div className="container pb120">
          <div className="login-container">
            <section className="login-form">
              <div className="col-md-12 ">
                <form id="login-form" action="https://www.kkfashion.vn/dang-nhap?back=my-account" method="post"
                      className="user-form m-auto" style={{width: "50%"}} >
                  <div className="form-title">
                    <span>Đăng nhập vào tài khoản của bạn</span>
                    <a href="https://www.kkfashion.vn/khoi-phuc-mat-khau">Quên mật khẩu?</a>
                  </div>
                  <section>
                    <div className="form-body">
                      <div className="row">
                        <div className="form-group">
                          <label className="col-sm-4"><i className="fa fa-user-o"></i>Email/ Tel<span
                              className="text-danger">*</span></label>
                          <div className="col-sm-8">
                            <div className="form-wrapper">
                              <input className="form-control" name="email"
                                     type="text" value="" maxLength="128"/>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="clearfix"></div>
                      <div className="row">
                        <div className="form-group">
                          <label className="col-sm-4"><i className="fa fa-lock"></i>Mật khẩu<span
                              className="text-danger">*</span></label>
                          <div className="col-sm-8">
                            <div className="form-wrapper">
                              <input className="form-control" name="password" type="password"
                                     value="" maxLength="128"  minLength="6" />
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="form-footer">
                      <div>
                        <input type="hidden" name="submitLogin" value="1"/>
                        <button id="submit-login" className="form-control-submit first-btn" data-link-action="sign-in"
                                type="submit" fdprocessedid="7egks">
                          Đăng nhập
                        </button>
                        <a className="second-btn fright" href="https://www.kkfashion.vn/dang-nhap?create_account=1">Tạo
                          tài khoản mới</a>
                      </div>
                    </div>
                  </section>
                </form>
              </div>
            </section>
          </div>
        </div>
      </div>







      <div className="container h-100">
        <div className="row justify-content-center h-100 align-items-center">
          <div className="col-sm-8 col-lg-5">
            <div className="card bg-primary">
              <div className="card-header text-white">
                <h4 className="card-title mb-0">
                  <i className="bi-grid-3x3-gap-fill"></i> Login
                </h4>
              </div>
              <div className="card-body bg-white rounded-bottom">
                <p className="text-center text-danger">{message}</p>
                <form onSubmit={submitHandler}>
                  <Input
                    inputRef={usernameRef} //tạo lính canh để theo dõi
                    id="txtUserName"
                    label="User name"
                    type="text"
                    autoComplete="off"
                    placeholder="Enter your username"
                    lastRow
                    // labelSize = "4"
                  />
                  <Input
                    inputRef={passwordRef}
                    id="txtPassword"
                    label="Password"
                    type="password"
                    placeholder="Enter your password"
                    lastRow
                  />
                  {/* 
                    <Input id="txtNote" rows="2" label="Note" /> */}

                  <div className="row">
                    <div className="offset-sm-3 col-auto">
                      <CustomButton type="submit" color="primary" isLoading={isWaiting} disabled={isWaiting} >
                        Sign dsd
                      </CustomButton>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Login;
