import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {useDispatch} from "react-redux";
import {logout} from "../store/auth";
import {Nav} from "react-bootstrap";
import settingService from "../services/settingService";
const Ads1 = (props) => {
    const id = "BANNER_SALES";
    const [ads1,setAds1] = useState([])

    useEffect(() => {
        settingService.getAds(id).then((res) => {
            setAds1(res.data)
            }
        );
    }, [id]);
    return (
        <>
            <div className="background-block">
                <div className="latest-block-list latest-home-block-list">
                    <div className="container block-list">
                        <div className="row">
                            <div className="col-md-7 text-right">
                                <div className="video-container">
                                    <div className="blance-video" data-yt-title="SOAK UP THE SUN"
                                         data-yt-url="https://www.youtube.com/embed/7p09GmbTsA8?feature=oembed&autoplay=1">
                                        <img width="100%"
                                             src="https://www.kkfashion.vn/img/banner/d90a00a9ea3c9886fcfc3049097734a6.jpg"
                                             alt="SOAK UP THE SUN"/>
                                        <div className="button-play-video">
                                            <img className="lozad" width="100" height="100"
                                                 data-src="./image/button-vodeo2.png"
                                                 src="/img/transparent.png" alt="Play Video" />
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-5">
                                <div className="video-content">
                                    <h2 className="fontutm">SOAK UP THE SUN</h2>
                                    <p>BST “Soak Up the Sun” của K&amp;K Fashion sẽ mang nàng đến một khung cảnh thiên nhiên
                                        rộng lớn hơn, bao la hơn cùng những thiết kế bay bổng để nàng được hòa mình, thư
                                        giãn thoải mái giữa cái nắng nóng của mùa hè năm nay. </p>
                                </div>
                            </div>
                            <div className="cleafix"></div>
                        </div>
                    </div>
                </div>
            </div>


        </>
    );
};

export default Ads1;
